import React, { useState, useEffect } from 'react';
import './contactus.css';

const Contactus = () => {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const isMobile = windowWidth <= 576; // Adjust the breakpoint for mobile devices
  const isTablet = windowWidth > 576 && windowWidth <= 992; // Adjust the breakpoints for tablets
  const isDesktop = windowWidth > 992; // Adjust the breakpoint for desktops

  const form = {
    color: '#fff',
    // Common styles for all devices
    padding: '20px',
  };

  // Styles for mobile devices
  if (isMobile) {
    Object.assign(form, {
      backgroundColor: 'red',
      fontSize: '16px',
      paddingTop: '170px',
      // Add more mobile-specific styles as needed
    });
  }

  // Styles for tablet devices
  if (isTablet) {
    Object.assign(form, {
      backgroundColor: 'blue',
      fontSize: '18px',
      // Add more tablet-specific styles as needed
    });
  }

  // Styles for desktop devices
  if (isDesktop) {
    Object.assign(form, {
      backgroundColor: 'green',
      fontSize: '20px',
      // Add more desktop-specific styles as needed
    });
  }

  return (
    <>
      <div className="container">
        <form className="form" style={form}>
          <span style={{ fontWeight: "800" }}>Contact <span style={{ fontWeight: "300" }}>Us</span></span>
        </form>
      </div>
    </>
  );
}

export default Contactus;
