import React, { useState, useEffect } from "react";
import './team.css';
import img01 from '../../../assets/img/aboutus/team/ancil.png';
import img02 from '../../../assets/img/aboutus/team/sundas.png';
import img03 from '../../../assets/img/aboutus/team/borna.png';
import img04 from '../../../assets/img/aboutus/team/neethu.png';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const Team = () => {
  const [isSlider, setIsSlider] = useState(true);

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 2550) {
        setIsSlider(true);
      } else {
        setIsSlider(false);
      }
    };
    

    window.addEventListener('resize', handleResize);
    handleResize(); // Call the function once on initial load

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const settings = {
    
    infinite: true,
    speed: 5000,
    slidesToShow: isSlider ? 1 : 4,
    slidesToScroll: 2,

    responsive: [
        {
            breakpoint: 1920, // Breakpoint for screens smaller than 768px
            settings: {
              slidesToShow: 4,
              spaceBetween: 550,
            },
          },
          {
            breakpoint: 1200, // Breakpoint for screens smaller than 768px
            settings: {
              slidesToShow: 3,
              spaceBetween: 550,
            },
          },
          {
            breakpoint: 992, // Breakpoint for screens smaller than 768px
            settings: {
              slidesToShow: 3,
            },
          },
        {
          breakpoint: 768, // Breakpoint for screens smaller than 768px
          settings: {
            slidesToShow: 3,
          },
        },
        {
          breakpoint: 480, // Breakpoint for screens smaller than 480px
          settings: {
            slidesToShow: 2,
          },
        },
        {
            breakpoint: 380, // Breakpoint for screens smaller than 480px
            settings: {
              slidesToShow: 2,
            },
          },
      ],
  };

  return (
    <>
      {isSlider ? (
        <Slider {...settings}>
          <div className="img000">
            <img src={img01} alt="Image 1" />
          </div>
          <div className="img000">
            <img src={img02} alt="Image 2" />
          </div>
          <div className="img000">
            <img src={img03} alt="Image 3" />
          </div>
          <div className="img000">
            <img src={img04} alt="Image 4" />
          </div>
          <div className="img000">
            <img src={img04} alt="Image 5" />
          </div>
        </Slider>
      ) : (
        <div className="team-grid">
          <img src={img01} alt="Image 1" />
          <img src={img02} alt="Image 2" />
          <img src={img03} alt="Image 3" />
          <img src={img04} alt="Image 4" />
          <img src={img01} alt="Image 1" />
          <img src={img02} alt="Image 2" />
          <img src={img03} alt="Image 3" />
          <img src={img04} alt="Image 4" />
        </div>
        
      )}

    </>
  );
}

export default Team;
