import React, { useState } from 'react';
import './slider.css'; // Import the CSS file for styling
import image01 from '../../../assets/img/common/NE@3x.png'
import image02 from '../../../assets/img/common/NE@3x.png'
import image03 from '../../../assets/img/common/NE@3x.png'




    const images = [
        image01,
        image02,
        image03
      ];

const ImageSlider = ({ images }) => {
  const [currentImage, setCurrentImage] = useState(0);

  const nextImage = () => {
    setCurrentImage((currentImage + 1) % images.length);
  };

  const previousImage = () => {
    setCurrentImage((currentImage - 1 + images.length) % images.length);
  };

  return (
    <div className="slider">
      <button onClick={previousImage}>Previous</button>
      <img src={images[currentImage]} alt="Slider" />
 
    </div>
  );
};

export default ImageSlider;
