import React, { useState, useEffect } from 'react';
import './contactus.css';
import PhoneInput from 'react-phone-number-input';
import 'react-phone-input-2/lib/style.css';


const Contactus = () => {
    
  
  const CustomDropdownIcon = () => {
    // Custom implementation for the dropdown icon
    return <div>Custom Dropdown Icon</div>;
  };
  
  const CustomSelectArrow = () => {
    // Custom implementation for the select arrow
    return <div>Custom Select Arrow</div>;
  };
  
  const CustomCountryCodeAddon = () => {
    // Custom implementation for the country code addon
    return <div>Custom Country Code Addon</div>;
  };
  
   
  const [value, setValue] = useState()
  const [country, setCountry] = useState('');
  const handleOnChange = (phone, countryObj) => {
    setValue(phone);
    setCountry(countryObj.name);
  };
  const countryOptions = [
    { value: 'SA', label: 'Saudi Arabia', flag: 'https://example.com/flags/sa.png' },
    { value: 'AE', label: 'United Arab Emirates', flag: 'https://example.com/flags/ae.png' },
    { value: 'QA', label: 'Qatar', flag: 'https://example.com/flags/qa.png' },
    { value: 'KW', label: 'Kuwait', flag: 'https://example.com/flags/kw.png' },
    { value: 'BH', label: 'Bahrain', flag: 'https://example.com/flags/bh.png' },
    { value: 'OM', label: 'Oman', flag: 'https://example.com/flags/om.png' },
  ];

  const [selectedCountryCode, setSelectedCountryCode] = useState('');


  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [name, setName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [email, setEmail] = useState('');
  const [dropdownValue, setDropdownValue] = useState('');
  const [message, setMessage] = useState('');
   
  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const isMobile = windowWidth <= 576; // Adjust the breakpoint for mobile devices
  const isTablet = windowWidth > 576 && windowWidth <= 992; // Adjust the breakpoints for tablets
  const isDesktop = windowWidth > 992; // Adjust the breakpoint for desktops

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
  };
  const form = {
    color: '#fff',
    // Common styles for all devices
    padding: '20px',
  };

  // Styles for mobile devices
  if (isMobile) {
    Object.assign(form, {
      backgroundColor: '',
      fontSize: '28px',
      paddingTop: '103px',
      // Add more mobile-specific styles as needed
    });
  }

  // Styles for tablet devices
  if (isTablet) {
    Object.assign(form, {
      backgroundColor: '',
      fontSize: '28px',
      // Add more tablet-specific styles as needed
    });
  }

  // Styles for desktop devices
  if (isDesktop) {
    Object.assign(form, {
      backgroundColor: '',
      fontSize: '28px',
      // Add more desktop-specific styles as needed
    });
  }

  return (
    <>
      <div className="container" >
        <form className="form" style={{ color: '#fff', padding: '20px' }}>
          <span className='contactus' style={{ fontWeight: "800", justifyContent:"center",display:"flex"}}>Contact <span style={{ fontWeight: "300" }}>&nbsp;Us</span></span>
          <div className="row">
          <div className={isMobile ? "col-12" : "col-md-6"}>

              <div className="form-group">
                <input
                  className='form_input'
                  type="text"
                  id="name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                  placeholder='Full Name'
                />
              </div>
              <div className="form-group phone_data">
                 
              <PhoneInput
  className="form_input phone_input"
  placeholder="Enter phone number"
  onChange={setValue}
  countries={countryOptions}
  country={'yourDefaultCountryCode'}
  enableAreaCodes={true}
  countryCodeEditable={false}
  renderDropdownIcon={<CustomDropdownIcon />}
  renderCountrySelectArrow={<CustomSelectArrow />}
  renderCountryCodeAddon={<CustomCountryCodeAddon />}
/>

                
              </div>
            </div>
            <div className={isMobile ? "col-12" : "col-md-6"}>
              <div className="form-group ">
                <input
                  className='form_input'
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  placeholder='Email Address'
                />
              </div>
              <div className="form-group">
                <select
                  className='form_input form_input custom-dropdown'
                  id="dropdown"
                  value={dropdownValue}
                  onChange={(e) => setDropdownValue(e.target.value)}
                 
                >
                  <option value="" style={{color:"#fff"}}>Choose your Service</option>
                  <option value="option1">Web Development</option>
                  <option value="option2"className='form_option_text'  style={{ borderWidth: "2px", borderStyle: "solid", borderColor: "yellow", width: "100%", height: "100%" }}>App Development</option>
                  <option value="option3"className='form_option_text'>Digital Marketing</option>
                  <option value="option1"className='form_option_text'>Social Media Management</option>
                  <option value="option2"className='form_option_text'>Media Production</option>
                  <option value="option3"className='form_option_text'>Graphic Design</option>
                </select>
              </div>
            </div>
            <div className="form-group">
              <textarea
              className='form_message'
                id="message"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                required
              />
            </div>
          </div>
          <div className='submit_button'>
          <button className='form_submit' type="submit">Get a quote</button>
          </div>
        </form>
      </div>
    </>
  );
}

export default Contactus;
