import React, { useState, useEffect } from 'react';
import './contactus.css';

const Contactus = () => {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [name, setName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [email, setEmail] = useState('');
  const [dropdownValue, setDropdownValue] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const isMobile = windowWidth <= 576; // Adjust the breakpoint for mobile devices
  const isTablet = windowWidth > 576 && windowWidth <= 992; // Adjust the breakpoints for tablets
  const isDesktop = windowWidth > 992; // Adjust the breakpoint for desktops

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
  };

  return (
    <>
      <div className="container" style={{justifyContent:"center", display:"flex"}}>
        <form className="form" style={{ color: '#fff', padding: '20px' }}>
          <span style={{ fontWeight: "800" }}>Contact <span style={{ fontWeight: "300" }}>Us</span></span>
          <div className="row">
          <div className={isMobile ? "col-12" : "col-md-6"}>

              <div className="form-group">
                <input
                  className='form_input'
                  type="text"
                  id="name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  className='form_input'
                  type="tel"
                  id="phone"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  required
                />
              </div>
            </div>
            <div className={isMobile ? "col-12" : "col-md-6"}>
              <div className="form-group">
                <input
                  className='form_input'
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <select
                  className='form_input'
                  id="dropdown"
                  value={dropdownValue}
                  onChange={(e) => setDropdownValue(e.target.value)}
                  required
                >
                  <option value="">Select</option>
                  <option value="option1">Option 1</option>
                  <option value="option2">Option 2</option>
                  <option value="option3">Option 3</option>
                </select>
              </div>
            </div>
            <div className="form-group">
              <textarea
                id="message"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                required
              />
            </div>
          </div>
          <button type="submit">Submit</button>
        </form>
      </div>
    </>
  );
}

export default Contactus;
