import React, { useState } from 'react';

import './App_design.css';
import "@fontsource/poppins";
import image1 from '../../assets/img/app_design/Group 1284@3x.png';
import image2 from '../../assets/img/app_design/Group 1285@3x.png';
import image3 from '../../assets/img/app_design/Group 1286@3x.png';
import image4 from '../../assets/img/app_design/Group 1287@3x.png';



  
const AppDesign = () =>{
    const [selectedImg, setSelectedImg] = useState(null);
    const handleImgClick = (imgSrc) => {
        setSelectedImg(imgSrc);
      };
    return(
        <div className="container container-fluid">
            <div className="ty_works">
                <ul className="ty_works_ul">
                    <li><a id="all">All</a></li>
                    <li><a>Websites</a></li>
                    <li><a>Mobile Apps</a></li>
                    <li><a>Digital Marketing</a></li>
                    <li><a>Social Media Management</a></li>
                    <li><a>Graphic Design</a></li>
                </ul>
                 <div>
              
              </div>
            </div>
            <div>
      <div className="gallery-container">
        <div className="gallery-row">
          <button onClick={(all) => handleImgClick('image1.jpg')}>
            <img src={image1} alt="Image 1" />
          </button>
          <button onClick={() => handleImgClick('image2.jpg')}>
            <img src={image2} alt="Image 2" />
          </button>
          <button onClick={() => handleImgClick('image3.jpg')}>
            <img src={image3} alt="Image 3" />
          </button>
        </div>
        <div className="gallery-row">
          <button onClick={() => handleImgClick('image4.jpg')}>
            <img src={image4} alt="Image 4" />
          </button>
          
        </div>
      </div>
      {selectedImg && (
        <div className="modal">
          <button onClick={() => setSelectedImg(null)}>Close</button>
          <img src={selectedImg} alt="Selected Image" />
        </div>
      )}
    </div>
        </div>

    )
} 
export default AppDesign;
