import React, { useRef, useEffect } from 'react';
import Marquee from "react-smooth-marquee"

function MarqueeSlider(props) {
  const sliderRef = useRef(null);

  useEffect(() => {
    let animationFrameId;
    const slider = sliderRef.current;

    function animate() {
      const firstChild = slider.firstChild;
      const firstChildWidth = firstChild.offsetWidth;
      const marginOffset = parseInt(getComputedStyle(firstChild).marginLeft);
      const sliderWidth = slider.offsetWidth;

      if (firstChildWidth + marginOffset <= 0) {
        slider.appendChild(firstChild);
        firstChild.style.marginLeft = '0';
      } else {
        firstChild.style.marginLeft = `${marginOffset - 1}px`;
      }

      animationFrameId = requestAnimationFrame(animate);
    }

    animationFrameId = requestAnimationFrame(animate);

    return () => {
      cancelAnimationFrame(animationFrameId);
    };
  }, []);

  return (
    <div className="marquee-slider" ref={sliderRef}>
      {props.children}
    </div>
  );
}


export default MarqueeSlider;
